package skills

import (
	"encoding/base64"
	"math/rand"
	"strconv"
	"strings"
	"sync"
	"time"

	tgbotapi "github.com/go-telegram-bot-api/telegram-bot-api"
)

var (
	diceOne   = "data:image/png;base64,"
	diceTwo   = "data:image/png;base64,"
	diceThree = "data:image/png;base64,"
	diceFour  = "data:image/png;base64,"
	diceFive  = "data:image/png;base64,"
	diceSix   = "data:image/png;base64,"
)

// RollDice ...
func RollDice(bot *tgbotapi.BotAPI, msg *tgbotapi.Message) {
	roll := diceIt()
	img := diceImg(roll)
	sendMsg := tgbotapi.NewPhotoUpload(msg.Chat.ID, img)
	sendMsg.Caption = "🎲 Тебе выпало " + strconv.Itoa(roll) + " 🎲"
	sendMsg.ReplyToMessageID = msg.MessageID
	bot.Send(sendMsg)
}

func diceIt() int {
	var onlyOnce sync.Once
	dice := []int{1, 2, 3, 4, 5, 6}
	onlyOnce.Do(func() {
		rand.Seed(time.Now().UnixNano()) // only run once
	})

	return dice[rand.Intn(len(dice))]
}

func diceImg(num int) tgbotapi.FileBytes {
	var img string
	switch num {
	case 1:
		img = diceOne
	case 2:
		img = diceTwo
	case 3:
		img = diceThree
	case 4:
		img = diceFour
	case 5:
		img = diceFive
	case 6:
		img = diceSix
	}

	idx := strings.Index(img, ";base64,")
	if idx < 0 {
		log.Error("Invalid Image")
	}
	unbasedImg, err := base64.StdEncoding.DecodeString(img[idx+8:])
	if err != nil {
		log.Error("cant unbase image")
	}
	b := tgbotapi.FileBytes{Name: "dice.png", Bytes: unbasedImg}
	return b
}
